import React from "react";
import { useTable, usePagination, useSortBy, useFilters} from 'react-table';

import { DefaultColumnFilter } from "./DefaultColumnFilter";
import { Table } from "./Table";

function Paginator({
    canPreviousPage,
    canNextPage,
    nextPage,
    previousPage,
    pageOptions,
    pageIndex,
}){
    {/* 
        Pagination can be built however you'd like. 
        This is just a very basic UI implementation:
    */}
    return (
        <div>
            <ul className="pagination">
                    {   pageIndex>0 ? (
                        <a className="page-change" onClick={() => previousPage()} disabled={!canPreviousPage}>{'<'}</a>
                    ) : <a className="disabled" onClick={() => previousPage()} disabled={!canPreviousPage}>{'<'}</a>
                    }
                    {pageIndex>0 ? (
                        <a className="page-links" onClick={() => previousPage()} disabled={!canPreviousPage}>{pageIndex}</a>
                    ) : null}
                    <a className="page-links" class="active">{pageIndex + 1}</a>
                    {pageIndex<pageOptions.length-1 ? (
                        <a className="page-links" onClick={() => nextPage()} disabled={!canNextPage}>{pageIndex + 2}</a>
                    ) : null}
                    {pageIndex<pageOptions.length-1 ? (
                    <a className="page-change" onClick={() => nextPage()} disabled={!canNextPage}>{'>'}</a>
                    ) :  <a className="disabled" onClick={() => nextPage()} disabled={!canNextPage}>{'>'}</a>
                }
            </ul>
        </div>
    )
}

export function Display({columns, data}) {

    const defaultColumn = React.useMemo(
        () => ({
            Filter: DefaultColumnFilter,
        }),
        []
    )

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        prepareRow,
        page,
        canPreviousPage,
        canNextPage,
        nextPage,
        previousPage,
        pageOptions,
        pageCount,
        setPageSize,
        state: { pageIndex, pageSize },
    } = useTable({
                columns,
                data,
                defaultColumn,
                initialState: { pageIndex: 0, pageSize: 3 },
            },
            useFilters,
            useSortBy,
            usePagination
    );

    return (
        <div>
            <div className="headerDropdown">
                <DefaultColumnFilter column={headerGroups[0].headers[2]} />
                <DefaultColumnFilter column={headerGroups[0].headers[4]} />
            </div>

            <Table getTableBodyProps={getTableBodyProps} headerGroups={headerGroups} prepareRow={prepareRow}
    page={page}
    getTableProps={getTableProps}/>

            <Paginator canPreviousPage={canPreviousPage} canNextPage={canNextPage} nextPage={nextPage}
                previousPage={previousPage} pageOptions={pageOptions} pageIndex={pageIndex}/>
        </div >
    )
}
